/*!

=========================================================
* BLK Design System React - v1.2.1
=========================================================

* Product Page: https://www.creative-tim.com/product/blk-design-system-react
* Copyright 2022 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/blk-design-system-react/blob/main/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import classnames from "classnames";
// reactstrap components
import {
  TabContent,
  TabPane,
  Container,
  Row,
  Col,
  Card,
  CardHeader,
  CardBody,
  Nav,
  NavItem,
  NavLink
} from "reactstrap";


export default function Tabs() {

  // the below info is SDK needed to call blockchain
  //const sdk = new ThirdwebSDK("goerli");
  //const contract = await sdk.getContract("0x5c035F0E633961b6F53fF2B7EfC7423229ecAAb8");



  //const [iconTabs, setIconsTabs] = React.useState(1);
  const [textTabs, setTextTabs] = React.useState(4);



  // This useEffect grabs all the addresses of our members holding our NFT.
useEffect(() => {
  if (!hasClaimedNFT) {
    return;
  }

  // Just like we did in the 7-airdrop-token.js file! Grab the users who hold our NFT
  // with tokenId 0.
  const getAllAddresses = async () => {
    try {
      const memberAddresses = await editionDrop?.history.getAllClaimerAddresses(
        0,
      );
      setMemberAddresses(memberAddresses);
      console.log('ðŸš€ Members addresses', memberAddresses);
    } catch (error) {
      console.error('failed to get member list', error);
    }
  };
  getAllAddresses();
}, [hasClaimedNFT, editionDrop?.history]);

// This useEffect grabs the # of token each member holds.
useEffect(() => {
  if (!hasClaimedNFT) {return;
 }

  const getAllBalances = async () => {
    try {
      const amounts = await token?.history.getAllHolderBalances();
      setMemberTokenAmounts(amounts);
      console.log('ðŸ‘œ Amounts', amounts);
    } catch (error) {
      console.error('failed to get member balances', error);
    }
  };
  getAllBalances();
}, [hasClaimedNFT, token?.history]);

// Now, we combine the memberAddresses and memberTokenAmounts into a single array
const memberList = useMemo(() => {

  if (address && (network?.[0].data.chain.id !== ChainId.Goerli))
            {
    return (
      <div className="unsupported-network">
        <h2>Please connect to Goerli</h2>
        <p>
          This dapp only works on the Goerli network, please switch networks
          in your connected wallet.
        </p>
      </div>
    );
  }
  return memberAddresses.map((address) => {
    // We're checking if we are finding the address in the memberTokenAmounts array.
    // If we are, we'll return the amount of token the user has.
    // Otherwise, return 0.
    const member = memberTokenAmounts?.find(({ holder }) => holder === address);

    return {
      address,
      tokenAmount: member?.balance.displayValue || '0',
    };
  });
}, [address, memberAddresses, memberTokenAmounts, network]);

  // This is the case where the user hasn't connected their wallet
  // to your web app. Let them call connectWallet.
  if (address && (network?.[0].data.chain.id !== ChainId.Goerli)) {
    return (

          <div
          className="section section-tabs"
          data-background-color="black"
          id="tabs-section"
          >

            <Container>

              <div className="title">
                <h3 className="mb-3">The DAO is currenty launched on the Goerli Testnet limited to 200ish users until phase 2.</h3>
              </div>
              <Row>
                <Col className="ml-auto mr-auto" md="10" xl="6">
                  <div className="mb-3">
                    <small className="text-uppercase font-weight-bold">
                      connect your wallet or mint your free nft membership here 
                    </small>
                  </div>
                  <iframe title="bdao"
                        src="https://ipfs.thirdwebcdn.com/ipfs/QmbAgC8YwY36n8H2kuvSWsRisxDZ15QZw3xGZyk9aDvcv7/erc1155.html?contract=0xB500BCd2A135b2b2b24da84E09AAFB05f0749633&chain=%7B%22name%22%3A%22Goerli%22%2C%22chain%22%3A%22ETH%22%2C%22rpc%22%3A%5B%22https%3A%2F%2Fgoerli.rpc.thirdweb.com%2F5a9bc94b87f7cbbbfbbc234bf1e07f0adf5f3cf3012c9f26f9fc9820d64df93a%22%5D%2C%22nativeCurrency%22%3A%7B%22name%22%3A%22Goerli+Ether%22%2C%22symbol%22%3A%22ETH%22%2C%22decimals%22%3A18%7D%2C%22shortName%22%3A%22gor%22%2C%22chainId%22%3A5%2C%22testnet%22%3Atrue%2C%22slug%22%3A%22goerli%22%7D&tokenId=0&theme=dark&primaryColor=black"
                        width="490px"
                        height="450px"
                        frameborder="0"
                  ></iframe>
                </Col>
                <Col className="ml-auto mr-auto" md="10" xl="6">
                  <div className="mb-3">
                    <small className="text-uppercase font-weight-bold">
                      Members list and Proposals will show here.
                    </small>
                  </div>
                  <Card>
                    <CardHeader>
                      <Nav className="nav-tabs-info" role="tablist" tabs>
                        <NavItem>
                          <NavLink
                            className={classnames({
                              active: textTabs === 4
                            })}
                            onClick={(e) => setTextTabs(4)}
                            href="#bdao"
                          >
                            Members
                          </NavLink>
                        </NavItem>
                        <NavItem>

                        </NavItem>
                        <NavItem>
                          <NavLink
                            className={classnames({
                              active: textTabs === 6
                            })}
                            onClick={(e) => setTextTabs(6)}
                            href="#pablo"
                          >
                            Proposals
                          </NavLink>
                        </NavItem>
                      </Nav>
                    </CardHeader>
                    <CardBody>
                      <TabContent className="tab-space" activeTab={"link" + textTabs}>
                        <TabPane tabId="link4">

                        <div className='rows'>
                          <div className='panels'>
                            <h2 className='h20'><img className="bDAO_logo_text" src={bDAO_logo} alt="BoostDAO logo"/>DAO Members</h2>
                            <table className="card">
                                <thead>
                                  <tr id="wrapper">
                                    <th id="one" className='cthw'> Wallet Address</th>
                                    <th id="two" className='cthw'> Voting Power</th>
                                  </tr>
                                </thead>
                            <div className='rlskroll'>  
                              <tbody>
                                {memberList.map((member) => {
                                  return (
                                    <tr key={member.address}>
                                      <td className='up'>{shortenAddress(member.address)}</td>
                                      <td className='up'>{member.tokenAmount}</td>
                                    </tr>
                                  );
                                })}
                              </tbody>
                            </div> 
                            </table>
                          </div>
                        </div>

                        </TabPane>

                        <TabPane tabId="link6">
                          <p>
                            I thinkvvv thatâ€™s a responsibility that I have, to push
                            possibilities, to show people, this is the level that
                            things could be at. So when you get something that has the
                            name Kanye West on it, itâ€™s supposed to be pushing the
                            furthest possibilities. I will be the leader of a company
                            that ends up being worth billions of dollars, because I
                            got the answers. I understand culture. I am the nucleus.
                          </p>
                        </TabPane>
                      </TabContent>
                    </CardBody>
                  </Card>
                </Col>
              </Row>
            </Container>
          </div>
  );
 }
}
