import React from "react";
import classnames from "classnames";
// reactstrap components
import {
  TabContent,
  TabPane,
  Container,
  Row,
  Col,
  Card,
  CardHeader,
  CardBody,
  Nav,
  NavItem,
  NavLink
} from "reactstrap";
import { 
  useAddress, 
  useNetwork,
  ConnectWallet, 
  Web3Button, 
  useContract, 
  useNFTBalance } from '@thirdweb-dev/react';
import { ChainId } from '@thirdweb-dev/sdk';
import { useState, useEffect, useMemo } from 'react';
import { AddressZero } from "@ethersproject/constants";


export default function Tabs() {

  //const [iconTabs, setIconsTabs] = React.useState(1);
  const [textTabs, setTextTabs] = React.useState(4);


  // the below info is SDK needed to call blockchain
  // Use the hooks thirdweb give us.
  const address = useAddress();
  console.log("ðŸ‘‹ Address:", address);
  const network = useNetwork();

  // Initialize our Edition Drop contract
  const editionDropAddress = "0xB500BCd2A135b2b2b24da84E09AAFB05f0749631";
  const { contract: editionDrop } = useContract(editionDropAddress, "edition-drop");
  // Initialize our token contract
  const { contract: token } = useContract('0x170E71c034791b1F9Fb19C1ae8cD5c2A66fb6dF4', 'token');
  //web app needs access to vote token
  const { contract: vote } = useContract("0x5c035F0E633961b6F53fF2B7EfC7423229ecAAb8", "vote");
  // Hook to check if the user has our NFT
  const { data: nftBalance } = useNFTBalance(editionDrop, address, "0")

  const hasClaimedNFT = useMemo(() => {
    return nftBalance && nftBalance.gt(0)
  }, [nftBalance])

  // Holds the amount of token each member has in state.
const [memberTokenAmounts, setMemberTokenAmounts] = useState([]);
// The array holding all of our members addresses.
const [memberAddresses, setMemberAddresses] = useState([]);

// A fancy function to shorten someones wallet address, no need to show the whole thing.
const shortenAddress = (str) => {
  return str.substring(0, 6) + '...' + str.substring(str.length - 4);
};

const [proposals, setProposals] = useState([]);
const [isVoting, setIsVoting] = useState(false);
const [hasVoted, setHasVoted] = useState(false);







  if (address && (network?.[0].data.chain.id !== ChainId.Goerli)) {
    return (

          <div
          className="section section-tabs"
          data-background-color="black"
          id="tabs-section"
          >

            <Container>

              <div className="title">
                <h3 className="mb-3">The DAO is currenty launched on the Goerli Testnet limited to 200ish users until phase 2.</h3>
              </div>
              <Row>
                <Col className="ml-auto mr-auto" md="10" xl="6">
                  <div className="mb-3">
                    <small className="text-uppercase font-weight-bold">
                      connect your wallet or mint your free nft membership here 
                    </small>
                  </div>
                  <iframe title="bdao"
                        src="https://ipfs.thirdwebcdn.com/ipfs/QmbAgC8YwY36n8H2kuvSWsRisxDZ15QZw3xGZyk9aDvcv7/erc1155.html?contract=0xB500BCd2A135b2b2b24da84E09AAFB05f0749633&chain=%7B%22name%22%3A%22Goerli%22%2C%22chain%22%3A%22ETH%22%2C%22rpc%22%3A%5B%22https%3A%2F%2Fgoerli.rpc.thirdweb.com%2F5a9bc94b87f7cbbbfbbc234bf1e07f0adf5f3cf3012c9f26f9fc9820d64df93a%22%5D%2C%22nativeCurrency%22%3A%7B%22name%22%3A%22Goerli+Ether%22%2C%22symbol%22%3A%22ETH%22%2C%22decimals%22%3A18%7D%2C%22shortName%22%3A%22gor%22%2C%22chainId%22%3A5%2C%22testnet%22%3Atrue%2C%22slug%22%3A%22goerli%22%7D&tokenId=0&theme=dark&primaryColor=black"
                        width="490px"
                        height="450px"
                        frameborder="0"
                  ></iframe>
                </Col>
                <Col className="ml-auto mr-auto" md="10" xl="6">
                  <div className="mb-3">
                    <small className="text-uppercase font-weight-bold">
                      Members list and Proposals will show here.
                    </small>
                  </div>
                  <Card>
                    <CardHeader>
                      <Nav className="nav-tabs-info" role="tablist" tabs>
                        <NavItem>
                          <NavLink
                            className={classnames({
                              active: textTabs === 4
                            })}
                            onClick={(e) => setTextTabs(4)}
                            href="#bdao"
                          >
                            Members
                          </NavLink>
                        </NavItem>
                        <NavItem>

                        </NavItem>
                        <NavItem>
                          <NavLink
                            className={classnames({
                              active: textTabs === 6
                            })}
                            onClick={(e) => setTextTabs(6)}
                            href="#pablo"
                          >
                            Proposals
                          </NavLink>
                        </NavItem>
                      </Nav>
                    </CardHeader>
                    <CardBody>
                      <TabContent className="tab-space" activeTab={"link" + textTabs}>
                        <TabPane tabId="link4">

                        <div className='rows'>
                          <div className='panels'>
                            <h2 className='h20'><img className="bDAO_logo_text" src={bDAO_logo} alt="BoostDAO logo"/>DAO Members</h2>
                            <table className="card">
                                <thead>
                                  <tr id="wrapper">
                                    <th id="one" className='cthw'> Wallet Address</th>
                                    <th id="two" className='cthw'> Voting Power</th>
                                  </tr>
                                </thead>
                            <div className='rlskroll'>  
                              <tbody>
                                {memberList.map((member) => {
                                  return (
                                    <tr key={member.address}>
                                      <td className='up'>{shortenAddress(member.address)}</td>
                                      <td className='up'>{member.tokenAmount}</td>
                                    </tr>
                                  );
                                })}
                              </tbody>
                            </div> 
                            </table>
                          </div>
                        </div>

                        </TabPane>

                        <TabPane tabId="link6">
                          <p>
                            I thinkvvv thatâ€™s a responsibility that I have, to push
                            possibilities, to show people, this is the level that
                            things could be at. So when you get something that has the
                            name Kanye West on it, itâ€™s supposed to be pushing the
                            furthest possibilities. I will be the leader of a company
                            that ends up being worth billions of dollars, because I
                            got the answers. I understand culture. I am the nucleus.
                          </p>
                        </TabPane>
                      </TabContent>
                    </CardBody>
                  </Card>
                </Col>
              </Row>
            </Container>
          </div>
  );
}
}
